import logo from '../airtable-logo.png';
import './App.css';
import styled from 'styled-components';

import Integrator from './Integrator';

const AppContainer = styled.div`
    display:flex;
    flex-wrap: no-wrap;
    justify-content:center;
    align-items:center;
`;

const MainHeading = styled.h1`
  font-size:32px;
`;



const apiKey = 'keyBupI08coJkltKa'; 

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // rec4sRpMUGLTGahXW
      patrick: [],
      // recFfOW7iuzI6a8cb
      neubs: [],
      // recfDa2Zeu940sdpA
      zack: [],
      // recbMjoumd8cb6pQg
      marco: [],
      // recUKD8ue91sXjx0x
      amaia: []
    }
  }

componentDidMount() {
fetch('https://api.airtable.com/v0/app9VtXS1vJyLgLgK/OB%20Integration%20Requests', {
headers: {
    Authorization: `Bearer ${apiKey}`
  }
}).then(response => {
    return response.json()}) 
    .then(jsonResponse => {
        if(jsonResponse.records) {
          console.log(jsonResponse.records);
          return jsonResponse.records.forEach(record => {
            console.log(record);
            if(record.fields['Claimed By:'][0] == 'rec4sRpMUGLTGahXW') {
              const patrickState = patrickState.push(record);
              this.setState({
                patrick: patrickState
              })
            }
          })

        }
    });
}


  render() {
    return (
      <div className="App">
          <img src={logo} className="App-logo" alt="logo" />
          <MainHeading>Integration Team Airtable Data</MainHeading>
          <AppContainer>
            <Integrator />
            <Integrator />
            <Integrator />
          </AppContainer>

      </div>
    );
  }
}

export default App;
